mapper.select:
SELECT
    count(*)
FROM
    tb_main_account s

    s.id is not null

        and s.account_id like concat('%',#{condition.account_id},'%')


        and s.status = #{condition.status}


        and s.create_time >= #{condition.start_time}


        and s.create_time <= #{condition.end_time}

SELECT s.id, s.account_id, s.status, s.create_time FROM tb_main_account s
s.id is not null
and s.account_id like concat('%',#{condition.account_id},'%')
and s.status = #{condition.status}
and s.create_time >= #{condition.start_time}
and s.create_time <= #{condition.end_time}
ORDER BY s.id DESC LIMIT #{current},#{size}
