mapper.select:
SELECT
    count(*)
FROM
    tb_operation_log as s

    s.id is not null

        and s.opt_type = #{condition.opt_type}


        and s.create_time >= #{condition.start_time}


        and s.create_time <= #{condition.end_time}

SELECT
    id,
    opt_type,
    opt_desc,
    create_time,
    update_time
FROM
    tb_operation_log as s

    s.id is not null

        and s.opt_type = #{condition.opt_type}


        and s.create_time >= #{condition.start_time}


        and s.create_time <= #{condition.end_time}


LIMIT #{current},#{size}
