mapper.select:
SELECT count(*) FROM tb_order as s left join (select order_sn, sum(count) as num from tb_order_item GROUP BY order_sn) as d on s.order_sn = d.order_sn left join tb_order_status_push as p on s.order_sn = p.order_sn left join tb_tracking_info as t on s.order_sn = t.order_sn
s.id is not null
and s.order_sn = #{condition.order_sn}
and IFNULL(p.status,s.STATUS) = #{condition.order_status}
and t.tracking_number = #{condition.tracking_number}
and s.shop_id = #{condition.shop_id}
and s.create_time >= UNIX_TIMESTAMP(STR_TO_DATE(#{condition.start_time}, '%Y-%m-%d %H:%i:%s'))
and s.create_time <= UNIX_TIMESTAMP(STR_TO_DATE(#{condition.end_time}, '%Y-%m-%d %H:%i:%s'))

SELECT s.id, s.order_sn, t.tracking_number, IFNULL(p.status,s.STATUS) as status, s.shop_id, s.create_time, s.isNew, IFNULL(d.num, 0) as totalCount FROM tb_order as s left join (select order_sn, sum(count) as num from tb_order_item GROUP BY order_sn) as d on s.order_sn = d.order_sn left join tb_order_status_push as p on s.order_sn = p.order_sn left join tb_tracking_info as t on s.order_sn = t.order_sn
s.id is not null
and s.order_sn = #{condition.order_sn}
and IFNULL(p.status,s.STATUS) = #{condition.order_status}
and t.tracking_number = #{condition.tracking_number}
and s.shop_id = #{condition.shop_id}
and s.create_time >= UNIX_TIMESTAMP(STR_TO_DATE(#{condition.start_time}, '%Y-%m-%d %H:%i:%s'))
and s.create_time <= UNIX_TIMESTAMP(STR_TO_DATE(#{condition.end_time}, '%Y-%m-%d %H:%i:%s'))
order by ${condition.orderBy_name}
LIMIT #{current},#{size}
