mapper.select:
SELECT
    count(*)
FROM
    tb_order as s

    s.id is not null

        and s.order_sn = #{condition.order_sn}


        and s.status = #{condition.order_status}


        and s.tracking_number = #{condition.tracking_number}


        and s.shop_id = #{condition.shop_id}


        and s.create_time >= #{condition.start_time}


        and s.create_time <= #{condition.end_time}

SELECT
    s.id, s.order_sn, s.tracking_number, IFNULL(p.status,s.STATUS) as status, s.shop_id, s.create_time, IFNULL(d.num, 0) as totalCount
FROM
    tb_order as s
left join
    (select order_sn, sum(count) as num from tb_order_item GROUP BY order_sn) as d
on
    s.order_sn = d.order_sn
left join  (select order_sn,status from tb_order_status_push where (order_sn, create_time) in (select order_sn, max(create_time) from tb_order_status_push group by order_sn)) as p
    on s.order_sn = p.order_sn

    s.id is not null

        and s.order_sn = #{condition.order_sn}


        and IFNULL(p.status,s.STATUS) = #{condition.order_status}


        and s.tracking_number = #{condition.tracking_number}


        and s.shop_id = #{condition.shop_id}


        and s.create_time >= #{condition.start_time}


        and s.create_time <= #{condition.end_time}



    order by ${condition.orderBy_name}

LIMIT #{current},#{size}
