mapper.select:
SELECT
    IFNUll(sum(count), 0)
FROM
    tb_order_item as s,
    tb_order as d
where
    s.order_sn = d.order_sn

    and s.item_id = #{item_id}


    and s.sku_name = #{sku_name}

    and
d.create_time >= #{start_time}
    and
d.create_time <= #{end_time}

SELECT
    s.item_id
FROM
    tb_order_item as s,
    tb_order as d
where
    s.order_sn = d.order_sn

    and d.create_time >= #{start_time}


    and d.create_time <= #{end_time}

group by s.item_id
having

    IFNUll(sum(s.count), 0) >= #{minSales}


    IFNUll(sum(s.count), 0) <= #{maxSales}


    IFNUll(sum(s.count), 0) >= #{minSales} and IFNUll(sum(s.count), 0) <= #{maxSales}

SELECT
    s.id
FROM
    tb_sku as s,
    tb_order_item as t,
    tb_order as d
where
    s.name = t.sku_name
    and t.order_sn = d.order_sn

    and d.create_time >= #{start_time}


    and d.create_time <= #{end_time}

group by t.id
having

    IFNUll(sum(count), 0) >= #{minSales}


    IFNUll(sum(count), 0) <= #{maxSales}


    IFNUll(sum(count), 0) >= #{minSales} and IFNUll(sum(count), 0) <= #{maxSales}
