mapper.select:
SELECT
    count(*)
FROM
    tb_photo as s


        s.name = #{condition.sku_id}


        and s.create_time >= #{condition.start_time}


        and s.create_time <= #{condition.end_time}


        and s.shop_id = #{condition.shop_id}

SELECT
    s.id,
    s.photoName,
    s.create_time,
    s.shop_id
FROM
    tb_cost as s


        s.start_time >= #{condition.start_time}


        s.end_time <= #{condition.end_time}


        and s.shop_id = #{condition.shop_id}


LIMIT #{current},#{size}
