mapper.select:
SELECT
    count(*)
FROM
    tb_product s

    s.id is not null

        and s.item_id = #{condition.item_id}


        and s.item_sku = #{condition.item_sku}


        and s.status = #{condition.item_status}


        and s.category_id = #{condition.category_id}


        and s.shop_id = #{condition.shop_id}


        and s.create_time >= #{condition.start_time}


        and s.create_time <= #{condition.end_time}

SELECT
    s.id, s.item_id, s.category_id, s.item_name, s.item_sku, s.mainImg_url, s.grade, s.shop_id, s.status, s.create_time, IFNULL(d.sale, 0) as salesVolume
FROM
    tb_product s
left join
    tb_product_extra_info d
on
    s.item_id = d.item_id

    s.id is not null

        and s.item_id = #{condition.item_id}


        and s.item_sku = #{condition.item_sku}


        and s.status = #{condition.item_status}


        and s.category_id = #{condition.category_id}


        and s.shop_id = #{condition.shop_id}


        and s.create_time >= #{condition.start_time}


        and s.create_time <= #{condition.end_time}



    order by ${condition.orderBy_name}

LIMIT #{current},#{size}
