mapper.select:
SELECT
    count(*)
FROM
    tb_rule as s

    s.id is not null

        and s.name = #{condition.rule_name}


        and s.grade = #{condition.rule_grade}


        and s.type = #{condition.rule_type}

SELECT
    id,
    name,
    grade,
    type,
    weight
FROM
    tb_rule as s

    s.id is not null

        and s.name = #{condition.rule_name}


        and s.grade = #{condition.rule_grade}


        and s.type = #{condition.rule_type}


LIMIT #{current},#{size}
