mapper.select:
SELECT
    count(*)
FROM
    tb_shop as s

    id is not null

        and s.shop_id = #{condition.shop_id}


        and s.name = #{condition.shop_name}


        and s.status = #{condition.status}


        and s.create_time >= #{condition.start_time}


        and s.create_time <= #{condition.end_time}


        and s.account_id = #{condition.account_id}

SELECT
    id,
    name,
    shop_id,
    status,
    create_time,
    account_id
FROM
    tb_shop as s

    id is not null

        and s.shop_id = #{condition.shop_id}


        and s.name = #{condition.shop_name}


        and s.status = #{condition.status}


        and s.create_time >= #{condition.start_time}


        and s.create_time <= #{condition.end_time}


LIMIT #{current},#{size}
