mapper.select:
SELECT
    count(*)
FROM
    tb_sku as s


        s.name = #{condition.sku_name}


        and s.create_time >= #{condition.start_time}


        and s.create_time <= #{condition.end_time}

SELECT
    s.id,
    s.name,
    s.relevance_ids,
    s.create_time
FROM
    tb_sku as s


        s.name = #{condition.sku_name}


        and s.create_time >= #{condition.start_time}


        and s.create_time <= #{condition.end_time}


LIMIT #{current},#{size}

SELECT s.id FROM tb_sku as s left join tb_model as m on s.name = m.sku_name where s.id is not null
and s.name = #{ruleCondition.skuName}
or s.name = #{ruleCondition.skuName}
group by s.id

SELECT s.id FROM tb_sku as s left join tb_model as m on s.name = m.sku_name left join tb_product as p on m.item_id = p.id where s.id is not null group by s.id having
min(p.create_time) >= #{ruleCondition.createStartTime}
min(p.create_time) <= #{ruleCondition.createEndTime}
min(p.create_time) >= #{ruleCondition.createStartTime} and min(p.create_time) <= #{ruleCondition.createEndTime}
