mapper.select:
SELECT
    count(*)
FROM
    tb_sku as s


        s.name = #{condition.sku_name}


        and s.create_time >= #{condition.start_time}


        and s.create_time <= #{condition.end_time}


        and s.shop_id = #{condition.shop_id}

SELECT
    id,
    name,
    relevance_ids,
    create_time
FROM
    tb_sku as s


        s.name = #{condition.sku_name}


        and s.create_time >= #{condition.start_time}


        and s.create_time <= #{condition.end_time}


        and s.shop_id = #{condition.shop_id}


LIMIT #{current},#{size}
