mapper.select:
SELECT
    count(*)
FROM
    tb_order as s

    s.id is not null

        and s.order_sn = #{condition.order_sn}


        and s.status = #{condition.order_status}


        and s.package_number = #{condition.package_number}


        and s.shop_id = #{condition.shop_id}


        and s.create_time >= #{condition.start_time}


        and s.create_time <= #{condition.end_time}

SELECT
    id,
    order_sn,
    package_number,
    status,
    shop_id,
    create_time
FROM
    tb_order as s

    s.id is not null

        and s.order_sn = #{condition.order_sn}


        and s.status = #{condition.order_status}


        and s.package_number = #{condition.package_number}


        and s.shop_id = #{condition.shop_id}


        and s.create_time >= #{condition.start_time}


        and s.create_time <= #{condition.end_time}


LIMIT #{current},#{size}
