mapper.select:
SELECT
    count(*)
FROM
    tb_product s

    s.id is not null

        and s.item_id = #{condition.item_id}


        and s.item_sku = #{condition.item_sku}


        and s.status = #{condition.item_status}


        and s.category_id = #{condition.category_id}


        and s.shop_id = #{condition.shop_id}


        and s.create_time >= #{condition.start_time}


        and s.create_time <= #{condition.end_time}

SELECT
    id,
    item_id,
    category_id,
    item_name,
    item_sku,
    mainImg_url,
    grade,
    shop_id,
    status,
    create_time
FROM
    tb_product s

    s.id is not null

        and s.item_id = #{condition.item_id}


        and s.item_sku = #{condition.item_sku}


        and s.status = #{condition.item_status}


        and s.category_id = #{condition.category_id}


        and s.shop_id = #{condition.shop_id}


        and s.create_time >= #{condition.start_time}


        and s.create_time <= #{condition.end_time}


LIMIT #{current},#{size}
